export class MockupDialog {
  constructor(http, // private dt: PresentationService,
  dialogRef, data) {
    this.http = http;
    this.dialogRef = dialogRef;
    this.data = data;
  }

  onNoClick() {
    this.dialogRef.close();
  }

  ngOnInit() {
    let self = this;
    this.refreshIntervalId = setInterval(function () {
      self.getMockupResults(self.data.task_id).subscribe(val => {
        self.serverData = val;

        if (self.serverData.result.status == "completed") {
          self.clearMockupCheck();
        }
      }, response => {}, () => {// console.log("The observable is now completed.");
      });
    }, 3000);
  }

  clearMockupCheck() {
    clearInterval(this.refreshIntervalId);
  }

  getMockupResults(taskId) {
    return this.http.post('http://localhost/smerch/printful.php', {
      "fetch": "check-mockup-task",
      "info": {
        "task_id": taskId
      }
    });
  }

}

