        clearInterval(this.refreshIntervalId);
    }
    getMockupResults(taskId) {
        return this.http.post('http://localhost/smerch/printful.php', {
            "fetch": "check-mockup-task",
            "info": { "task_id": taskId }
        });
    }
}
